import React, {Component} from 'react';
import util from '../util';
import Cart from '../components/Cart';

class Product extends Component{
    constructor(props){
        super(props);
        this.state = {
            products: [], 
            cartItems: [],
        };
      }
      componentWillMount(){
        fetch("http://localhost:3001/products").then(res => res.json())
        .then(data => this.setState({
          products: data,
        }));
      }
      handleIncrement = () => {
        this.setState({
            qty: this.state.insertProduct.total_qty += 1,
        })
        if (this.state.insertProduct.total_qty >= this.props.stock){
            this.setState({
                qty: this.state.insertProduct.total_qty = this.props.stock
            })
        }
        this.handleTotal()
    }
    handleTotal = () => {
        this.setState({
            totalPrice: this.state.insertProduct.total_price = 
            this.props.price * this.state.insertProduct.total_qty,
            id: this.state.insertProduct["id"] = new Date().getTime()
        })
    }
    handleDecrement = () => {
        this.setState({
            qty: this.state.insertProduct.total_qty -= 1,
        })
        if (this.state.insertProduct.total_qty <= 0) {
            this.setState({
                qty: this.state.insertProduct.total_qty = 0
            })
        }
    }

    handleChange = (event) => {
        this.setState({
            total_qty: this.state.insertProduct["total_qty"] = event.target.value,
        })
        this.handleTotal()
    }

    handleSave = () => {
        if (this.state.insertProduct.total_qty <= 0){
            alert("Minimum order is 1");
            this.handleDecrement()
        } else if (this.state.insertProduct.total_qty > this.props.stock){
            alert("Your Order Exceeds Existing Stock");
            this.handleIncrement()
        } else if (this.state.insertProduct.total_qty != 0 && this.state.insertProduct.total_qty <= this.props.stock){
            fetch("http://localhost:7002/cart", {
                method: "post",
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(this.state.insertProduct)
            }).then(response => {
                if (response.ok) {
                    alert("Product Was Added Successfully To Cart")
                    this.setState({
                        total_qty: this.state.insertProduct["total_qty"] = 0
                    })
                    this.handleTotal()
                    return response.json()
                } else {
                    alert(response.statusText)
                }
            })
        }
    }
    //   addToCart = (product) =>{
    //     const cartItems = this.state.cartItems.slice();
    //     let alreadyInCart = false;
    //     cartItems.forEach((item) =>{
    //         if(item._id === product._id){
    //             item.count++;
    //             alreadyInCart = true;
    //         }
    //     });
    //     if(!alreadyInCart){
    //         cartItems.push({...product, count: 1});
    //     }
    //     this.setState({cartItems});
    //   };
    render(){
        const productItems = this.state.products.map(product => (
            <div className="col-md-4" key={product.id}>
                <div className="product-image-wrapper">
                    <div className="single-products">
                        <div className="productinfo text-center">
                            <a
                                href={`#${product.id}`}
                                onClick={this.state.handleAddToCart}>
                            <img src={`/products/${product.img}`} />
                            <h2>{util.formatCurrency(product.price)}</h2>
                            <p> {product.productName} </p>
                            </a>
                            <div>
                                <p>Total Order: </p> <br></br>
                                <button className="button_decrement" onClick={this.handleDecrement}><b> - </b></button>&emsp;
                                <input type="text" id="total_qty" name="total_qty" 
                                value={this.state.insertProduct.total_qty} onChange={this.handleChange}></input>&emsp;
                                <button className="button_increment" onClick={this.handleIncrement}><b> + </b></button><br></br>
                                <p>Total Price: IDR {this.state.insertProduct.total_price}</p> <br></br>
                                <button className="btn btn-primary" type="submit" onClick={this.handleSave}>Add to Cart</button>
                            </div><br></br>
                        </div>
                    </div>
                </div>
            </div>
        ))
        return(
            <div>
                <section>
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-12">
                                <div className="features_items">
                                    <h2 className="title text-center">Features Items</h2>
                                    {productItems}
                                </div>
                            </div>
                            {/* <div className="col-sm-3">
                                <div className="left-sidebar">
                                    <div className="brands_products"> 
                                    <h2>Cart</h2>
                                    <Cart cartItems={this.state.cartItems} />
                                    </div> 
                                </div>
                            </div> */}
                        </div>
                    </div>
                </section>
            </div>
        )
    }
} export default Product
