import React, { Component } from 'react'

export default class Cart extends Component {
    state = {
        listCart: [],
        totalPrice: 0,
        numb: 0
    }
    getDataFromAPI = () => {
        fetch("http://localhost:3002/cart")
        .then(response => response.json())
        .then(jsonGetDataAPI => {
            this.setState({
                listCart: jsonGetDataAPI
            })
        })
    }
    componentDidMount(){
        this.getDataFromAPI();
    }
    render() {
        const {cartItems} = this.props;
        return (
            <div>
                <center>
                    {cartItems.length === 0 ? (
                        <div className="cart cart-header">
                            Cart is empty</div>
                    ) : (
                        <div className="cart cart-header">
                            You have {cartItems.length} in the cart {" "}
                        </div>
                    )}
                </center>
                <div>
                    <div className="cart">
                        <div className="cart-items">
                            {cartItems.map(item =>
                                <li key={cartItems._id}>
                                    <div>
                                        <img src={cartItems.img} alt={cartItems.productName}></img>
                                    </div>
                                    <div>
                                        <div>{cartItems.productName}</div>
                                        <button onClick={()=> this.props.removeFromCart(item)}>
                                            Remove
                                        </button>
                                    </div>
                                </li>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
